function MakeChild(name, grade, points, wins, losses, dyslexia, progress) {
    let Temp = new Child();

    Temp.username = name;
    Temp.subscription = true; //Bypassing add-a-card
    Temp.cohort = c1; //Tying the new child to the new cohort
    Temp.parent = ParentDefault; //Tying the new child to the new parent
    Temp.preferences = new Preferences();
    Temp.preferences.dyslexia = dyslexia;
    Temp.grade = grade;
    Temp.total_points = points;
    Temp.wins = wins;
    Temp.losses = losses;
    Temp.votes = 0;

    Temp.progress = new Progress();
    progress.forEach((name) => {
        Temp.progress[name] = true;
    });

    return Temp;
}

